import React, { Component } from "react";
import axios from "axios";

export default class Weather extends Component {
	constructor() {
		super();
		this.state = {weather: "Not yet gotten"};
	}

	handleButtonClick = () => {
		axios.get("/getWeather").then(response =>{
			this.setState({
				weather: response.data.daily[0].temp.day
			});
		});
	};
	render() {
		return (
			<div>
				<button onClick={this.handleButtonClick}>Get Weather</button>
				<h1> The weather is: {this.state.weather}</h1>
				</div>
			);
	}
}
