import React, { Component } from "react";
import axios from "axios";

export default class Weather extends Component {
	constructor(props) {
		super(props);

		this.handleChangeLat = this.handleChangeLat.bind(this);
        this.handleChangeLon = this.handleChangeLon.bind(this);
        this.handleButtonClick = this.handleButtonClick.bind(this);

		this.state = {lat: '',
					  lon: '',
	                  mean: 0,
	                  median: 0,
	                  mode: 0};
	}

	handleChangeLat = () => {
    this.setState({lat: this.target.value});
  };

  	handleChangeLon = () => {
    this.setState({lon: this.target.value});
  };

	handleButtonClick = () => {

		const userObject = {
            lon: this.state.lon,
            lat: this.state.lat
        };

		axios.post('/weather', userObject)
            .then((res) => {
            	console.log(res);
                this.setStat({lon: res.weather.lon, lat: res.weather.lat});
            }).catch((error) => {
                console.log(error)
            });
	};
	render() {
		return (
			<div>
				<label>
		          Enter Latitude and Longitude:
		          <input type="text" value={this.state.lat} onChange={this.handleChangeLat} />
		          <input type="text" value={this.state.lon} onChange={this.handleChangeLon} />
		        </label>
				<button onClick={this.handleButtonClick}>Get Weather</button>
				<div>Mean: {this.state.mean} </div>
			     <div>Median:{this.state.median} </div>
			     <div>Mode: {this.state.mode}</div>
				</div>
			);
	}
}
